import React, { useEffect, useState } from "react";

const Dropdown = ({ optionArray, onSelectedchange, selected }) => {
    const [open, setopen] = useState(false);

    useEffect(() => {
        document.body.addEventListener('click', () => {
            setopen(false);
        })
    })

    const renderOptions = optionArray.map(option => {
        if (selected.value == option.value) {
            return null;
        }
        return (
            <div key={option.value} className="item" onClick={() => {
                onSelectedchange(option)
            }}>
                {option.lable}
            </div>
        );
    });

    return (
        <div className="ui form">
            <div className="field">
                <div className="lable"> Select Lable Color </div>
                <div onClick={() => {
                    setopen(!open)
                }}
                    className={`ui selection dropdown ${open ? 'visible active' : ''}`} >
                    <i className="dropdown icon"></i>
                    <div className="text"> {selected.lable} </div>
                    <div className={`menu ${open ? 'visible transition' : ''}`}>
                        {renderOptions}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Dropdown
