import React,{ useState }  from "react";

const Dropdown = ({ optionArray,onSelectedchange }) => {

    const renderOptions = optionArray.map(option => {
        return (
            <div key={option.value} className="item">
                {option.lable}
            </div>
        );
    });

    return (
        <div className="ui form">
            <div className="field">
                <div className="lable"> Select Lable Color </div>
                <div className="ui selection dropdown visible ">
                    <i className="doepdown icon"></i>
                    <div className="text"> Select Lable Color </div>
                    <div className="manu visible transition">
                        {renderOptions}
                    </div>  
                </div>
            </div>

        </div>
    );
}

export default Dropdown
