import React, { useState } from "react";
import Dropdown from './Dropdown.js';

const Translate = () => {

    const dropdownOptions = [
        {lable: 'Afrikaans ', value: 'af',},
        {lable: 'Arabic', value: 'ar',},
        {lable: 'Hindi', value: 'hi',},
    ]

    const [language, setLanguate] = useState(dropdownOptions[0]);

    return (
        <div>
            <Dropdown selected={language} onSelectedchange={setLanguate} optionArray={dropdownOptions} lable={'Select Languy'}  />
        </div>
    );

}

export default Translate;
