import Accordion from './Components/Accordion.js'
import SearchComponent from './Components/Search.js'
import Dropdown from './Components/Dropdown.js'
import Translate from './Components/Translate.js'

const itemArray = [
    { id: "0", title: "title0", body: "body0" },
    { id: "1", title: "title1", body: "body1" },
    { id: "2", title: "title2", body: "body2" },
    { id: "3", title: "title3", body: "body3" },
]


const dropdownOptions = [
    { lable: "Green", value: "This is Green Color" },
    { lable: "Blue", value: "This is Blue Color" },
    { lable: "Red", value: "This is Red Color" },
    { lable: "Orange", value: "This is Orange Color" },
]

const showAccodion=()=>{
    if(window.location.pathname ==='/'){
        return <Accordion itemArray={itemArray} />
    }
}

const showDorpDown=()=>{
    if(window.location.pathname ==='/dorpDown'){
        return <Dropdown selected={selected} onSelectedchange={setselected} optionArray={dropdownOptions} lable={'Select Color'} />
    }
}

const showSearchComponent=()=>{
    if(window.location.pathname ==='/search'){
        return <SearchComponent />
    }
}

const showTranslate=()=>{
    if(window.location.pathname ==='/translate'){
        return <Translate />
    }
}

export default () => {
    const [selected, setselected] = useState(dropdownOptions[0]);
    const [showDorpDown, setshowDorpDown] = useState(true);

